mapper.select:
SELECT t3.*,t1.* FROM (
  select t.id,t.head_img_url,t.nick_name,t.user_id from wx_contact t


            t.create_user= ${userId}


   ) t1
left join customer t2 on t1.id = t2.wx_contact_id
left join  (

                select a1.*  from (
                    select a.id,a.customer_id,a.wx_username,a.msg_type,a.content,a.url,a.is_send,a.send_time,a.create_user FROM chat_records a


                            a.create_user= ${userId}


                ) a1 left join (
                    select b.* from chat_records b


                            b.create_user= ${userId}


                ) b1 ON a1.wx_username = b1.wx_username AND a1.send_time  <  b1.send_time

                GROUP BY a1.wx_username,a1.send_time,a1.id HAVING COUNT(b1.id)  <   1

                    ORDER BY a1.send_time DESC

    )t3 on t3.customer_id = t2.id
order by t3.send_time

SELECT * FROM ( SELECT r.*,wc.head_img_url,wc.nick_name FROM wx_contact wc INNER JOIN customer c ON c.wx_contact_id = wc.id INNER JOIN ( SELECT a.id,a.customer_id,a.wx_user_name, a.msg_type,a.content,a.url, a.is_send,a.send_time FROM chat_records a LEFT JOIN chat_records b ON a.wx_user_name = b.wx_user_name AND a.send_time > b.send_time
a.status = 1
AND a.create_user= ${userId}
GROUP BY a.wx_user_name, a.send_time HAVING COUNT(b.id) < 1
ORDER BY a.send_time DESC
) r ON c.id = r.customer_id WHERE 1 = 1 AND wc.status = 1 AND r.send_time >= '${startTime} 00:00:00' AND r.send_time <= '${endTime} 23:59:59'
AND wc.id IN
#{item}
ORDER BY INSTR ${orderByStr}
) t ORDER BY t.send_time ASC
